import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "./Intro.css";

// import required modules
import { Pagination } from "swiper";

const IntroText = ({num}) => {
    return (
        <div>
        <h2> STEP {num} </h2>
        <p> text가 들어갈 자리입니다. </p>
        </div>
    );
}


const Intro = () => {
  return (
    <>
      <Swiper
        direction={"vertical"}
        pagination={{
          clickable: true
        }}
        modules={[Pagination]}
        className="mySwiper"
      >
        <SwiperSlide><IntroText num = "1" /></SwiperSlide>
        <SwiperSlide><IntroText num = "2" /></SwiperSlide>
        <SwiperSlide><IntroText num = "3" /></SwiperSlide>
        <SwiperSlide><IntroText num = "4" /></SwiperSlide>
      </Swiper>
    </>
  );
}

export default Intro;
