import './Upload.css';
import { useState } from 'react';

const Option = ({optionName, dbName, example}) => {
  return (
    <div class = "option">
      <input type="text" id = "option" name = {dbName} placeholder = {"ex) " + example} />
      <div> {optionName} </div>
    </div>
  );
}

const Upload = () => {
  const [upload, setUpload] = useState(false);
  const [file, setFile] = useState("");

  const getName = () => {
    if(upload){
      setFile(document.getElementsByClassName('uploadedFile')[0].files[0].name);
    }
    return file;
  }

  return (
    <div class= "Upload">
      <div class = "left">
        <label for = "drop">
          <div class = "drop_box">
            {!upload ? "Click the Area to Upload" : getName()}
          </div>
        </label>
        <input type="file" name="uploadedFile" class = "uploadedFile" id = "drop" onChange = {() => setUpload(true)} multiple = {false} />
      </div>
      <div class ="right">
        <Option optionName = "Mouse Name" dbName = "mouse_name" example = "Jerry"/>
        <Option optionName = "Experiment Model" dbName = "experimental_model" example = "Drug Diseased"/>
        <Option optionName = "Heredity" dbName = "Heredity" example= "Agouti"/>
        <Option optionName = "Genotype" dbName = "Genotype" example = "Sox9"/>
        <input type = "submit" value = "UPLOAD" class = "submit" />
      </div>
    </div>
  );
}

export default Upload;
