import './Upload.css';
import { useState } from 'react';
import axios from "axios";

const Option = ({optionName, dbName, example, txt}) => {
  return (
    <div class = "option">
      <input type="text" name = {dbName} placeholder = {"ex) " + example}
      onChange={({txt}) => setMouse_name({txt}.target.value)}/>
      <div> {optionName} </div>
    </div>
  );
}

const Upload = () => {
  const [upload, setUpload] = useState(false);
  const [Mouse_name, setMouse_name] = useState("");
  const [Experimental_model, setExperimental_model] = useState("");
  const [Heredity, setHeredity] = useState("");
  const [Genotype, setGenotype] = useState("");
  const [UploadedFile, setUploadedFile] = useState("");
  return (
    <div class= "Upload">
      <div class = "left">
        <label htmlFor = "drop">
          <div class = {!upload ? "drop_box" : "drop_box_selected"}>
            <div>
              {!upload ? "Click the Area to Upload" : document.getElementsByClassName('uploadedFile')[0].files[0].name}<br />
              <span>{upload ? "" : ".mp4 or .avi"} </span>
            </div>
          </div>
        </label>
        <input type="file" name="uploadedFile" class = "uploadedFile" id = "drop" accept=".mp4, .avi"
        onChange = {(file) => setUploadedFile(file.target.files)}/>
      </div>
      <div class ="right">
        <Option optionName = "Mouse Name" dbName = "mouse_name" example = "Jerry" txt = {text1}/>
        <Option optionName = "Experiment Model" dbName = "experimental_model" example = "Drug Diseased" txt = {text2}/>
        <Option optionName = "Heredity" dbName = "Heredity" example= "Agouti" txt = {text3}/>
        <Option optionName = "Genotype" dbName = "Genotype" example = "Sox9" txt = {text4}/>
        <input type = "submit" value = "UPLOAD" class = "submit"
        onClick = {() => axios.post("http://127.0.0.1:8000/main/", {
                            mouse_name: Mouse_name,
                            experimental_model: Experimental_model,
                            heredity: Heredity,
                            genotype: Genotype,
                            uploadedFile : UploadedFile})
        }/>
      </div>
    </div>
  );
}

export default Upload;
